for planet, _ in pairs( Falcon.NPCs ) do
    for _, npc in pairs( Falcon.NPCs[planet] ) do

        if not npc.Menus then
            local ShouldSpawn = math.random(1, 3)
            if ShouldSpawn == 1 then Falcon.NPCs[_] = nil continue end
        end

        if not npc.Name then
            npc.Name = names[math.random(1, #names)]
        end

        if not npc.Occupation then
            local isEmployed = math.random(1, 3)
            if isEmployed == 1 then
                npc.Occupation = "Unemployed"
            else
                npc.Occupation = occupations[math.random(1, #occupations)]
            end
        end

